mapper.sql:
SELECT T.*
FROM CMNT_MGMT_COMMENT T
WHERE 1=1

    AND T.mainId = #{params.mainid}


    AND T.mainType = #{params.maintype}

mapper.select:
SELECT T.*, ISNULL(tp.thumbsupCnt, 0) thumbsupcnt, T2.memberId AS targetmemberid, T2.memberName AS targetmembername, T2.avatar AS targetavatar, (CASE WHEN EXISTS (SELECT 1 FROM cmnt_mgmt_thumbsup tp2 WHERE T.id = tp2.commentId AND (tp2.memberId = #{params.memberid} OR tp2.openId = #{params.openid})) THEN 1 ELSE 0 END) isthumbsup FROM CMNT_MGMT_COMMENT T LEFT JOIN cmnt_mgmt_comment T2 ON (T.parentId = T2.id) LEFT JOIN (SELECT COUNT(*) thumbsupCnt, commentId FROM cmnt_mgmt_thumbsup GROUP BY commentId) tp ON (T.id = tp.commentId) WHERE (T.approvedStatus = 0 or T.approvedStatus = 2)
AND T.mainId = #{params.mainid}
AND T.mainType = #{params.maintype}
ORDER BY T.createDate DESC
