mapper.sql:
SELECT T.ID, T.INTEGRAL_ID, T.MEMBER_ID, a.member_name, a.real_name, a.mobile FROM QKJVIP_MEMBER_INTEGRALUSER T join qkjvip_member a on (T.MEMBER_ID=a.member_id) WHERE 1=1 --(a.statusflag = 0 OR a.statusflag = 2)
AND T.INTEGRAL_ID = #{params.integralId}
AND T.MEMBER_ID = #{params.memberId}

mapper.select:
SELECT i.MEMBER_ID, m.openid, m.mobile FROM QKJVIP_MEMBER_INTEGRALUSER i
INNER JOIN qkjvip_member m ON (i.MEMBER_ID = m.member_id)
WHERE (m.statusflag = 0 OR m.statusflag = 2) and i.INTEGRAL_ID = #{value}
