mapper.select:
SELECT * FROM qkjvip_member_basic
WHERE cellphone in

	#{item.cellphone}

update.foreach:
update qkjvip_member_basic
realname=#{item.realname}, typename=#{item.typename}, gradename=#{item.gradename}, integral=#{item.integral}, realtotalprice=#{item.realtotalprice}, sex=#{item.sex}
where cellphone=#{item.cellphone} and shopname=#{item.shopname}
