mapper.select:
SELECT ORG_NO FROM SYS_ROLE_ORG WHERE ROLE_ID IN (SELECT DISTINCT UR.role_id FROM SYS_USER_ROLE UR LEFT JOIN SYS_ROLE_MENU RM ON UR.ROLE_ID = RM.ROLE_ID LEFT JOIN SYS_MENU M ON RM.MENU_ID = M.MENU_ID WHERE 1=1
AND UR.USER_ID = #{params.userId}
AND M.PERMS LIKE '%${params.userPerm}%'
)
